import React from "react";
import {
  Button,
  Card,
  CardContent,
  Grid,
  TextField,
  Typography,
} from "@mui/material";

export default function TaskForm() {
  return (
    <Grid
      container
      direction="columns"
      alignItems="center"
      justifyContent="center"
    >
      <Grid item xd={3}>
        <Card
          sx={{ mt: 5 }}
          style={{
            backgroundColor: "#064663",
            padding: "1rem",
          }}
        >
          <Typography variant="5" textAling="center" color="#ECB365">
            Crear nota
          </Typography>
          <CardContent>
            <form>
              <TextField
                variant="filled"
                label="Escriba el titulo"
                sx={{
                  display: "block",
                  margin: ".5rem 0",
                }}
              />
              <TextField
                variant="filled"
                label="Escriba la descripcion"
                multiline
                rows={4}
                sx={{
                  display: "block",
                  margin: ".5rem 0",
                }}
              />

              <Button variant="contained" color="primary" type="submit">
                Guardar
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
